mapper.select:
SELECT (C_E_DATE - C_S_DATE + 1)
FROM PRE_C_INFO
WHERE C_NUM = #{c_num}

SELECT COUNT(1)
FROM C_STU_INFO
WHERE (ATTENDSTATUS = 'absent' OR ATTENDSTATUS IS NULL) AND C_NUM = #{c_num} AND STU_ID = #{stu_id}

