mapper.update:
UPDATE PRE_C_INFO
SET C_NAME = #{c_name}, C_OVERVIEW = #{c_overview}, C_RE_DATE = TO_CHAR(#{c_re_date},'YYYY/MM/DD'),
	C_S_DATE = TO_CHAR(#{c_s_date},'YYYY/MM/DD'), C_E_DATE = TO_CHAR(#{c_e_date},'YYYY/MM/DD'), C_E_TIME = #{c_e_time},
	C_CO_TIME = #{c_co_time}, S_CLOSED = #{s_closed}
WHERE C_NUM = #{c_num}

mapper.select:
SELECT C_E_TIME, C_CO_TIME
FROM PRE_C_INFO
WHERE C_NUM = #{c_num}

SELECT P.*, M.NAME
FROM PRE_C_INFO P
JOIN MEMBERINFO M
ON P.PRO_ID = M.ID
WHERE P.C_NUM = #{c_num}
AND P.PRO_ID = #{pro_id}
AND C_STATUS = #{c_status}
AND (M.NAME LIKE '%'||#{searchContent}||'%'
	 OR P.C_NAME LIKE '%'||#{searchContent}||'%')
